import React, { Component } from 'react';
import axios from 'axios';

getDisplayName =(WrappedComponent) => {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

export function withApiProgres(WrappedComponent,apiPath) {
    return class   extends Component {
       static displayName= `ApiProgres(${getDisplayName(WrappedComponent)})`;

      state = {
        pendingApiCall: false
      };
    
      
      
      componentDidMount() {
        axios.interceptors.request.use(request => {
          this.updateApiCallFor(request.url, true);
          console.log(request.url)
          console.log(this.props.path)
          return request;
        });
    
        axios.interceptors.response.use(
          response => {
            this.updateApiCallFor(response.config.url, false);
            return response;
          },
          error => {
            this.updateApiCallFor(error.config.url, false);
            throw error;
          }
        );
      }
    
      updateApiCallFor = (url, inProgress) => {
        if (url === apiPath) {
          this.setState({ pendingApiCall: inProgress });
        }
      };
    
      render() {
        const { pendingApiCall } = this.state;
        // return <div>{React.cloneElement(this.props.children, { pendingApiCall })}</div>;
        return <WrappedComponent pendingApiCall={pendingApiCall} {...this.props} />
      }
    }
}
