import React, { Component } from 'react'
import Input from '../component/Input';
import {login} from '../api/apiCalls';

class LoginPage extends Component {
    state={
        username:null,
        password:null
    }
     onChange = (event) =>{
        const {name, value} = event.target;
        this.setState({
            [name]: value
        })
    }
    onClickLogin = async event => {
        event.preventDefault();
        const { username, password } = this.state;
        const creds = {
          username,
          password
        };
        this.setState({
          error: null
        });
        try {
          await login(creds);
        } catch (apiError) {
          this.setState({
            error: apiError.response.data.message
          });
        }
      };
    render() {
        return (
            <div className='container'>
                <form>
                    <h1 className='text-center'>Login</h1>
                    <Input label="Kullanıcı Adı" name="username" onChange={this.onChange} />
                    <Input label="Parola" name="password" type="password" onChange={this.onChange} />
                    <div className='text-center'>
                    <button className='btn btn-primary' onClick={this.onClickLogin}>Login</button>    
                    </div>
                    
                </form>
                
            </div>
        )
    }
}
export default  LoginPage;
